import React from 'react';
import Table from 'react-bootstrap/Table';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';

//add functionality to the buttons -- add debt adds a row

class App extends React.Component {
  constructor() {
    super();
    this.state = {
      error: null,
      isLoaded: false,
      allSelected: false,
      items: [],
      newItemInProgress: false,
      idInProgress: this.state.items[this.state.items.length-1].id + 1,
      creditorInProgress: '',
      firstNameInProgress: '',
      lastNameInProgress: '',
      minPayInProgress: 0,
      balanceInProgress: 0
    };
    this.addDebt = this.addDebt.bind(this);
    this.saveDebt = this.saveDebt.bind(this);
    this.removeDebt = this.removeDebt.bind(this);
  }

  componentDidMount() {
    fetch('https://raw.githubusercontent.com/StrategicFS/Recruitment/master/data.json')
      .then(res => res.json())
      .then(
        (result) => {
          this.setState({
            isLoaded: true,
            items: result
          });
        },
        (error) => {
          this.setState({
            isLoaded: true,
            error
          });
        }
      )
  }

  addDebt() {
    this.setState((state) => ({...state, newItemInProgress: true}))
  }

  saveDebt() {

  }

  removeDebt() {
    let newItems = this.state.items.filter(item => item.checked !== true);
    this.setState((state) => ({...state, items: newItems}));
  }

  handleChange(event) {
    this.setState({
      [event.target.name]: event.target.value
    })
  }

  render() {
    const { error, isLoaded, items, newItemInProgress } = this.state;
    console.log('items-->', items)

    if (error) {
      return <div>Error: {error.message}</div>;
    } else if (!isLoaded) {
      return <div>Loading...</div>;
    } else {
      return (
      <div className="App">
        <div className="banksTable">
          <Table striped bordered hover>
            <thead>
              <tr>
                <th>
                  <input
                    name="selectAll"
                    type="checkbox"
                    checked={!!this.state.allSelected}
                    onChange={() => {
                      const items = [...this.state.items];
                      if (this.state.allSelected) {
                        items.map(item => item.checked = false)
                      } else {
                        items.map(item => item.checked = true)
                      }
                      this.setState((state) => ({...state, items: items, allSelected: !this.state.allSelected}));
                    }}
                  />
                </th>
                <th>Creditor</th>
                <th>First Name</th>
                <th>Last Name</th>
                <th>Min Pay%</th>
                <th>Balance</th>
              </tr>
            </thead>
            <tbody>
              {items.map(item => (
                <tr key={item.idInProgress}>
                  <td></td>
                  <td>{item.creditorName}</td>
                  <td>{item.firstName}</td>
                  <td>{item.lastName}</td>
                  <td>{item.minPaymentPercentage.toFixed(2)}%</td>
                  <td>{item.balance.toFixed(2)}</td>
                </tr>
              ))}
              {newItemInProgress && 
                <tr key={item.id}>
                <td></td>
                <td>
                  <form>
                    <input
                    type="text"
                    name="name"
                    onChange={this.handleChange}
                    value={this.state.creditorInProgress}
                    />
                  </form>
                </td>
                <td>
                  <form>
                      <input
                      type="text"
                      name="name"
                      onChange={this.handleChange}
                      value={this.state.firstNameInProgress}
                      />
                    </form>
                </td>
                <td>
                  <form>
                      <input
                      type="text"
                      name="name"
                      onChange={this.handleChange}
                      value={this.state.lastNameInProgress}
                      />
                    </form>
                </td>
                <td>
                  <form>
                  <input
                  type="text"
                  name="name"
                  onChange={this.handleChange}
                  value={this.state.minPaymentInProgress}
                  />
                  </form>
                </td>
                <td>
                  <form>
                      <input
                      type="text"
                      name="name"
                      onChange={this.handleChange}
                      value={this.state.balanceInProgress}
                      />
                  </form>
                </td>
              </tr> }
            </tbody>
          </Table>
        </div>
        <div className="tableButtons">
          {newItemInProgress && <button onClick={this.saveDebt}>Save Debt</button>}
          <button onClick={this.addDebt}>Add Debt</button> <button onClick={this.removeDebt}>Remove Debt(s)</button>
        </div>
        <div className="balanceTotal">
          <Table hover>
            <td>Balance:</td>
            <td>${items.filter(item => item.checked === true).reduce((sum, cur) => sum + cur.balance, 0).toFixed(2)}</td>
          </Table>
        </div>
        <div className="rowCounts">
        Total Row Count: {items.length} | Check Row Count: {items.filter(item => item.checked === true).length}
        </div>
      </div>
    );
  }
  }
}

export default App;
